import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { StudentService } from '../student.service';
import { studentmodel } from '../student'; 
import { Router } from '@angular/router';

@Component({
  selector: 'app-add-update-student',
  templateUrl: './add-update-student.component.html',
  styleUrls: ['./add-update-student.component.css']
})
export class AddUpdateStudentComponent implements OnInit {
  studentform !: FormGroup;
  value2: any;
  value = undefined;
  title!: string;
  edit!: string;
  buttonsave!: string;

  constructor(private service: StudentService , public activatedRoute:ActivatedRoute,private formbulider: FormBuilder, private router:Router){}
  ngOnInit() {

    this.value2 = this.activatedRoute.params;
    this.value = this.value2["_value"].id;


    this.studentform = this.formbulider.group({
      name:['',Validators.required,Validators.maxLength(10)],
      emailid:['',Validators.required,Validators.maxLength(20)],
      phoneNo:['',Validators.required,Validators.maxLength(10)],
      city:['',Validators.required,Validators.maxLength(10)]
    })

    if(this.value != undefined){
      this.setdataedit();

    }

  }
  save(data:any){
      this.service.addstudent(data).subscribe(res => {
        alert('Student Add successfully')
        this.router.navigate([''])
      });   
  }

  setdataedit(){

    this.service.getByID(this.value).subscribe((val) => {
    this.studentform.controls['name'].setValue(val[0].name);
    this.studentform.controls['emailid'].setValue(val[0].emailid);
    this.studentform.controls['phoneNo'].setValue(val[0].phoneNo);
    this.studentform.controls['city'].setValue(val[0].city);
    this.edit='0'
    })
  }
  updatedata(data:studentmodel){
    data.id = this.value;
    this.service.updatestudent(data).subscribe(res => {    
      alert('Student Update successfully')
      this.router.navigate([''])
     });
  }
  get err(){
    return this.studentform.controls;
  }

}
<div style="width: 40%; margin: auto; border: 1px solid black;">

    <h4>{{title}}</h4>
    <form [formGroup]="studentform">
        <!-- <input type="hidden" class="form-control" formControlName="id"   > -->
        <div class="form-group mb-2">
          <label>Name</label>
          <input type="text" class="form-control" formControlName="name"  placeholder="Enter  Name">
          <!-- <div *ngIf="err['name'].invalid && err['name'].touched" class="alert alert-danger">
            <small>Name is Invalid</small>
          </div> -->
        </div>

        <div class="form-group mb-2">
            <label>Email</label>
            <input type="text" class="form-control" formControlName="emailid"  placeholder="Enter Email">
            <div *ngIf="err['emailid'].invalid && err['emailid'].touched" class="alert alert-danger">
                <small>Email Id is Invalid</small>
            </div>
          </div>
        <div class="form-group mb-2">
            <label>Moblie</label>
            <input type="number" class="form-control" formControlName="phoneNo"  placeholder="Enter Mobile">
            <div *ngIf="err['phoneNo'].invalid && err['phoneNo'].touched" class="alert alert-danger">
                <small>Mobile is Invalid</small>
            </div>
        </div>
        <div class="form-group mb-2">
            <label >City</label>
            <input type="text" class="form-control" formControlName="city" placeholder="Enter City ">
            <div *ngIf="err['city'].invalid && err['city'].touched" class="alert alert-danger">
                <small>City is Invalid</small>
            </div>
        </div>
        <div class="form-group mb-2 " style="height: 20px;">
            <div style="float: right;">
                <a class="btn closebutton" [routerLink]="['']">close</a>&nbsp; &nbsp;

                <!-- <button type="button" class="btn btn-secondary"  >Close</button> -->
                <button type="button" *ngIf=" edit !='0'"  [disabled]="this.studentform.invalid" class="btn btn-primary" (click)="save(this.studentform.value)"  >Save</button>
                <button type="button" *ngIf=" edit =='0'" [disabled]="this.studentform.invalid" class="btn btn-primary" (click)="updatedata(this.studentform.value)">
                    Update
                </button>
            </div>
        </div>
    </form>
</div>
