import { Component,OnInit } from '@angular/core';
import { StudentService } from '../student.service';

@Component({
  selector: 'app-student-list',
  templateUrl: './student-list.component.html',
  styleUrls: ['./student-list.component.css']
})
export class StudentListComponent implements OnInit {
  studentData !: any[] ;
  constructor(private service: StudentService){}

  ngOnInit(){
    this.refreshStudentList()
  }
  refreshStudentList() {
    this.service.getStudentList().subscribe(data => {
      this.studentData = data;
    }) ;
    // ,
    // (error)=>{
    //    alert("Please Check your Internet Connectionb")
    // })  
  }
  deleteStudent(st:any){
    if (confirm('Are you sure??')) {
      this.service.deleteStuent(st.id).subscribe(data => {
        this.refreshStudentList();
      });
    }
  }
}

<table class="table">
    <thead>
      <tr>
        <th>S.Id</th>
        <th>Name</th>
        <th>Email</th>
        <th>Moblile</th>
        <th>City</th>
        <th>Action</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let st of studentData,  index as i">
        <th>{{i +1 }}</th>
        <td>{{st.name}}</td>
        <td>{{st.emailid}}</td>
        <td>{{st.phoneNo}}</td>
        <td>{{st.city}}</td>
        <td>
          <a [routerLink]="['/edit',st.id]" class="btn btn-primary">Edit</a> &nbsp; &nbsp;
          <button type="button"  class="btn btn-danger" (click)="deleteStudent(st)">
              Delete
          </button>
        </td>
      </tr> 
    </tbody>
  </table>
