import { Component, OnInit } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { StudentService } from '../student.service';

@Component({
  selector: 'app-add-update-student',
  templateUrl: './add-update-student.component.html',
  styleUrls: ['./add-update-student.component.css']
})
export class AddUpdateStudentComponent implements OnInit {
  studentform: any;
  constructor(private service: StudentService , public activatedRoute:ActivatedRoute,private formbulider: FormBuilder,){}
  ngOnInit() {
    this.studentform = this.formbulider.group({
      name:['',Validators.required],
      emailid:['',Validators.required],

    })
  }


}
<form [formGroup]="studentform">
    <div class="form-group mb-2">
      <label for="name" class="form-label">Student Name</label>
      <input type="text" class="form-control" formControlName="name"  placeholder="Enter  Name">
    </div>
    <div class="form-group mb-2">
        <label for="name" class="form-label"> Email</label>
        <input type="text" class="form-control" formControlName="emailid"  placeholder="Enter Email">
      </div>
    <div class="form-group mb-2">
        <label for="email" class="form-label">Phone</label>
        <input type="email" class="form-control" formControlName=""  placeholder="Enter  Email">
    </div>
    <div class="form-group mb-2">
        <label for="number" class="form-label">Age</label>
        <input type="text" class="form-control" formControlName="age" placeholder="Enter Age ">
    </div>
    <div class="form-group mb-2">
        <label for="city" class="form-label">City</label>
        <input type="text" class="form-control" formControlName="city" placeholder="Enter Employee City">
    </div>

    <div class="form-group mb-2">
        <label for="phone" class="form-label"> Phone Number</label>
        <input type="text" class="form-control" formControlName="phone" placeholder="Enter Phone Number">
    </div>
    <div class="form-group mb-2">
        <label for="phone" class="form-label">State</label>
        <input type="text" class="form-control" formControlName="state" placeholder="Enter Phone Number">
    </div>
  </form>
