import { Component, OnInit } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { StudentService } from '../student.service';

@Component({
  selector: 'app-add-update-student',
  templateUrl: './add-update-student.component.html',
  styleUrls: ['./add-update-student.component.css']
})
export class AddUpdateStudentComponent implements OnInit {
  studentform: any;
  constructor(private service: StudentService , public activatedRoute:ActivatedRoute,private formbulider: FormBuilder,){}
  ngOnInit() {
    this.studentform = this.formbulider.group({
      name:['',Validators.required],
      emailid:['',Validators.required],

    })
  }


}
<div style="width: 40%; margin: auto; border: 1px solid black;">
    <form [formGroup]="studentform">
        <div class="form-group mb-2">
          <label for="name" class="form-label">Student Name</label>
          <input type="text" class="form-control" formControlName="name"  placeholder="Enter  Name">
        </div>
        <div class="form-group mb-2">
            <label for="name" class="form-label"> Email</label>
            <input type="text" class="form-control" formControlName="emailid"  placeholder="Enter Email">
          </div>
        <div class="form-group mb-2">
            <label for="email" class="form-label">Moblie</label>
            <input type="email" class="form-control" formControlName="phoneNo"  placeholder="Enter  Email">
        </div>
        <div class="form-group mb-2">
            <label for="number" class="form-label">City</label>
            <input type="text" class="form-control" formControlName="city" placeholder="Enter Age ">
        </div>
        <div class="form-group mb-2">
            <button type="button" class="btn btn-secondary"  data-bs-dismiss="modal">Close</button>
            <button type="button" (click)="addEmployee(this.employeeform.value)" *ngIf="val.employeeid =='0'"  [disabled]="this.employeeform.invalid" class="btn btn-primary"  >Save</button>
            <button  *ngIf="val.employeeid !='0'" class="btn btn-primary"   data-bs-dismiss="modal">
              Update
            </button>
        </div>
    </form>
</div>
